    parametre = 'ginettedu2666'
    sortie = ['chocolat au lait', 'chips saveur wasabi', 'chocolat noir 70%', 'cranberry', 'semoule de blé spéciale\xa0\ndessert', 'lentilles\xa0verte']
    
